import React, { useState } from 'react';
import './GameOverScreen.css';

const GameOverScreen = () => {
  const [selectedDate, setSelectedDate] = useState({
    year: new Date().getFullYear(),
    month: new Date().getMonth() + 1,
    day: new Date().getDate()
  });

  const handleRetry = () => {
    alert('다시 도전하기!');
  };

  const generateYears = () => {
    const currentYear = new Date().getFullYear();
    const years = [];
    for (let i = currentYear; i >= currentYear - 100; i--) {
      years.push(i);
    }
    return years;
  };

  const generateMonths = () => {
    return Array.from({ length: 12 }, (_, i) => i + 1);
  };

  const generateDays = () => {
    const daysInMonth = new Date(selectedDate.year, selectedDate.month, 0).getDate();
    return Array.from({ length: daysInMonth }, (_, i) => i + 1);
  };


  return (
    <div className="game-over-container">

      {/* 상단 텍스트 */}
      <div className="text-section">
        <h1 className="failure-text">오늘의 운세를 확인해 볼까요? </h1>
        <h2 className="retry-question">with 짐보</h2>
      </div>

      {/* BAGSPACE 로고 */}
      <div className="character-section">
        <div className="logo-container">
          <img 
            src="/BAGSPACE_LOGO_ORIGINAL.jpg" 
            alt="BAGSPACE Logo" 
            className="bagspace-logo"
          />
        </div>
      </div>

      {/* 생일 입력 섹션 */}
      <div className="birthday-section">
        <h3 className="birthday-title">생년월일을 입력해주세요</h3>
        <div className="date-picker-container">
          <div className="date-picker">
            <select 
              value={selectedDate.year} 
              onChange={(e) => setSelectedDate({...selectedDate, year: parseInt(e.target.value)})}
              className="date-select"
            >
              {generateYears().map(year => (
                <option key={year} value={year}>{year}년</option>
              ))}
            </select>
          </div>
          <div className="date-picker">
            <select 
              value={selectedDate.month} 
              onChange={(e) => setSelectedDate({...selectedDate, month: parseInt(e.target.value)})}
              className="date-select"
            >
              {generateMonths().map(month => (
                <option key={month} value={month}>{month}월</option>
              ))}
            </select>
          </div>
          <div className="date-picker">
            <select 
              value={selectedDate.day} 
              onChange={(e) => setSelectedDate({...selectedDate, day: parseInt(e.target.value)})}
              className="date-select"
            >
              {generateDays().map(day => (
                <option key={day} value={day}>{day}일</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* 하단 텍스트 및 버튼 */}
      <div className="bottom-section">
        <p className="prize-text">딱 2단계만 더 깨면 BAGSPACE 여행 상품 받아요</p>
        <button className="retry-button" onClick={handleRetry}>
          공유하고 다시 도전하기
        </button>
      </div>
    </div>
  );
};

export default GameOverScreen;
